import { useAuth0 } from "@auth0/auth0-react";


const LoginButton = () => {
    const {isAuthenticated} = useAuth0();
    const login = () => {
        const {loginWithRedirect, isAuthenticated, user } = useAuth0();
        loginWithRedirect();
        fetch("/addUser", {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify ({
                name: user.name,
                authID: user.sub
            })
        })
    }
    return(
        !isAuthenticated && (
            <button onClick={() => login()}>
                Sign In
            </button>
        )

    )
}

export default LoginButton
