import { useAuth0 } from "@auth0/auth0-react";


const LoginButton = () => {
    const {loginWithRedirect, isAuthenticated, user } = useAuth0();
    function addUser() {
        if(!user) {
            console.log("Hello World");
            setTimeout(addUser, 100)
        }
        else {
            console.log("in else");
            fetch("/addUser", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify ({
                    name: user.name,
                    authID: user.sub
                })
            })
        }
    }
    const login = async() => {

        loginWithRedirect()
        addUser()

    }
    return(
        !isAuthenticated && (
            <button onClick={() => login()}>
                Sign In
            </button>
        )

    )
}

export default LoginButton
